var arr = []; polygons.forEach(function(state){ obj = {}; obj.id = state.id; obj.count = state.count; obj.coordinates0 = state.geom.coordinates[0].map(projection); obj.coordinates1 = square(obj.coordinates0)[0]; obj.d0 = &quot;M&quot; + obj.coordinates0.join(&quot;L&quot;) + &quot;Z&quot;; obj.d1 = &quot;M&quot; + obj.coordinates1.join(&quot;L&quot;) + &quot;Z&quot;; obj.area = square(obj.coordinates0)[1]; arr.push(obj); }); return arr; } function square(coordinates){ var area = obj.count * obj.count *20; area &lt; 0 ? area = area * -1 : area = area; var r = Math.sqrt(area) / 2.5; var centroid = d3.polygonCentroid(coordinates); var x = centroid[0]; var y = centroid[1]; var len = coordinates.length; var square = squareCoords(x, y, r, len); return [square, area]; } function squareCoords(x, y, r, len){ var square = []; var topLf = [x - r, y - r]; var topRt = [x + r, y - r]; var botRt = [x + r, y + r]; var botLf = [x - r, y + r]; for (var i = 0; i &lt; len / 4; i++){ square.push(botRt); } for (var i = 0; i &lt; len / 4; i++){ square.push(botLf); } for (var i = 0; i &lt; len / 4; i++){ square.push(topLf); } for (var i = 0; i &lt; len / 4; i++){ square.push(topRt); } return square; } function stepChange(oldStep, newStep, obj){ d3.selectAll(&quot;.state&quot;).remove(); obj.forEach( function(d){ transitionPath(drawPath(d), d[&quot;d&quot; + oldStep], d[&quot;d&quot; + newStep], duration); drawLabels(d, oldStep, newStep); } ); } function stepUpdate(){ var currStep = +d3.select(&quot;body&quot;).attr(&quot;step&quot;), newStep; var newStep = currStep == 0 ? 1 : 0; d3.select(&quot;body&quot;).attr(&quot;step&quot;, newStep); return [currStep, newStep]; } function transitionPath(path, d0, d1, duration){ path .attr(&quot;d&quot;, d0) .transition().duration(duration) .attr(&quot;d&quot;, d1); } &lt;/script&gt; &lt;/body&gt;

